mapper.select:
SELECT 

FROM research a

WHERE a.id = #{id}

SELECT
FROM research a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM research a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE research SET 	
	name = #{name},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE research SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
